method RotateRightOnce(a: array<int>)
    modifies a;
    ensures  a.Length > 0 ==> a[..] == old([a[a.Length - 1]] + a[..a.Length - 1]);
{
    if a.Length == 0 {
        return;
    }
    var temp := a[a.Length - 1];
    var i := a.Length - 1;
    while i > 0
        decreases i;
        invariant 0 <= i <= a.Length - 1;
        invariant a[i + 1..] == old(a[i..a.Length - 1]);
        invariant a[..i + 1] == old(a[..i + 1]);
    {
        a[i] := a[i - 1];
        i := i - 1;
    }
    a[0] := temp;
}
method RotateRightMany(a: array<int>, rot: int)
    modifies a;
    ensures  a.Length > 0 ==> a[..] == old(a[a.Length - rot % a.Length..] + a[..a.Length - rot % a.Length]);
{
    if a.Length == 0 {
        return;
    }
    var i := 0;
    while i < rot % a.Length
        decreases rot % a.Length - i;
        invariant 0 <= i <= rot % a.Length;
        invariant a[..] == old(a[a.Length - i..] + a[..a.Length - i]);
    {
        RotateRightOnce(a);
        i := i + 1;
    }
}

method seqIntoArray<A>(s: seq<A>, a: array<A>, index: nat)
  requires index + |s| <= a.Length
  modifies a
  ensures a[..] == old(a[..index]) + s + old(a[index + |s|..])
{
  var i := index;
  while i < index + |s|
    invariant index <= i <= index + |s| <= a.Length
    invariant a[..] == old(a[..index]) + s[..i - index] + old(a[i..])
  {
    label A:
    a[i] := s[i - index];
    calc {
      a[..];
    ==  
      old@A(a[..])[i := s[i - index]];
    ==  
      (old(a[..index]) + s[..i - index] + old(a[i..]))[i := s[i - index]];
    ==  { assert old(a[..index]) + s[..i - index] + old(a[i..]) == (old(a[..index]) + s[..i - index]) + old(a[i..]); }
      ((old(a[..index]) + s[..i - index]) + old(a[i..]))[i := s[i - index]];
    ==  { assert |old(a[..index]) + s[..i - index]| == i; }
      (old(a[..index]) + s[..i - index]) + old(a[i..])[0 := s[i - index]];
    == { assert old(a[i..])[0 := s[i - index]] == [s[i - index]] + old(a[i..])[1..]; }
      (old(a[..index]) + s[..i - index]) + [s[i - index]] + old(a[i..])[1..];
    ==  { assert old(a[i..])[1..] == old(a[i + 1..]); }
      (old(a[..index]) + s[..i - index]) + [s[i - index]] + old(a[i + 1..]);
    ==  
      old(a[..index]) + (s[..i - index] + [s[i - index]]) + old(a[i + 1..]);
    ==  { assert s[..i - index] + [s[i - index]] == s[..i + 1 - index]; }
      old(a[..index]) + s[..i + 1 - index] + old(a[i + 1..]);
    }
    i := i + 1;
  }
}

