    b[0]:=78; b[1]:=56; b[2]:=34; b[3]:=12;
    assert b[..]==[78,56,34,12];
    ms := multiset(b[..]);
    Shuffle(b);
    assert b[..]==[12,34,56,78];
    assert Sorted(a, 0, a.Length);
    assert ms == multiset(b[..]);
    print b[..], '\n';
}

module stack {
  datatype stack<T> = EmptyStack | stack(top: T, num_elems: nat, rest: stack<T>)
  datatype Option<T> = Some(elem: T) | None
  method push<T>(stk: stack<T>, elem: T) returns (res: stack<T>)
    ensures res != EmptyStack
    ensures (stk == EmptyStack) ==> (res == stack(elem, 1, EmptyStack()))
  {
    match stk {
      case EmptyStack => {
        res := stack(elem, 1, EmptyStack());
      }
      case stack(top, num_elems, rest) => {
        res := stack(elem, num_elems + 1, stack(top, num_elems, rest));
      }
    }
  }
  method pop<T>(stk: stack<T>) returns (elem: Option<T>, res: stack<T>) {
    match stk {
      case EmptyStack => {
        elem := None();
        res := EmptyStack;
      }
      case stack(top, num_elems, rest) => {
        elem := Some(top);
        res := rest;
      }
    }
  }
}

method RemoveDuplicates(nums: array<int>) returns (num_length: int)
  modifies nums
  requires forall i, j | 0 <= i < j < nums.Length :: nums[i] <= nums[j]
  ensures nums.Length == old(nums).Length
  ensures 0 <= num_length <= nums.Length
  ensures forall i, j | 0 <= i < j < num_length :: nums[i] != nums[j]
  ensures forall i | 0 <= i < num_length :: nums[i] in old(nums[..])
  ensures forall i | 0 <= i < nums.Length :: old(nums[i]) in nums[..num_length]
{
  if nums.Length <= 1 {
    return nums.Length;
  }
  var last := 0;
  var i := 1;
  ghost var nums_before := nums[..];
  while i < nums.Length
    invariant 0 <= last < i <= nums.Length
    invariant nums[i..] == nums_before[i..]
    invariant forall j, k | 0 <= j < k <= last :: nums[j] < nums[k]
    invariant forall j | 0 <= j <= last :: nums[j] in nums_before[..i]
    invariant forall j | 0 <= j < i :: nums_before[j] in nums[..last+1]
  {
    if nums[last] < nums[i] {
      last := last + 1;
      nums[last] := nums[i];
      assert forall j | 0 <= j < i :: nums_before[j] in nums[..last];
      assert forall j | 0 <= j <= i :: nums_before[j] in nums[..last+1];
    }
    i := i + 1;
  }
  return last + 1;
}
