method Flip_Flop(C: bool, I: bool, T0: bool) returns (T: bool)
	ensures T == ((C && I) || (!C && T0))
{
	var X, Y, Z := false, false, true; 
	if C { 
		if I {
			X := true;
		}
		else {
			Y := true;
		}
	}
	if !X { 
		if !T0 {
			Z := false;
		}
	}
	if !Y { 
		if Z {
			T := true;
		}
		else {
			T := false;
		}	
	}
	else {
		T := false;
	}
	assert X == (C && I);
	assert Y == (C && !I);
	assert Z == !(!X && !T0) == X || T0;
	assert T == (!Y && Z);
	assert T == (!(C && !I) && ((C && I) || T0));
	assert T == ((!C || I) && ((C && I) || T0));
	assert T == ((C && I) || (!C && T0));
}
method Flip_Flop_2(C0: bool, I0: bool, C1: bool, I1: bool, T0: bool) returns (T: bool)
	requires C0 
	ensures (!C1 ==> T == I0) && (C1 ==> T == I1) 
{
	assert C0 ==> C0 || C1 || T0 == I0;
	T := T0;
	assert C0 ==> C0 || C1 || T == I0;
	assert C0 || C1 || T == I0;
	assert C0 || C1 || (T && I0) || (!T && !I0);
	assert C1 || (I0 && C0) || (I0 && T) || (!I0 && C0) || (!I0 && !T);
	assert C1 || (I0 && (C0 || T)) || (!I0 && (C0 || !T));
	assert C1 || (I0 && ((C0 && I0) || (!C0 && T))) 
		|| (!I0 && !((C0 && I0) || (!C0 && T)));
	T := Flip_Flop(C0, I0, T);
	assert C1 || (I0 && T) || (!I0 && !T);
	assert (C1 && I1) 
		|| (C1 && !I1) 
		|| (!C1 && I0 && T)
		|| (!C1 && !I0 && !T);
	assert (C1 && I1 && ((C1 && I1) || (!C1 && T))) 
		|| (C1 && !I1 && !((C1 && I1) || (!C1 && T))) 
		|| (!C1 && I0 && ((C1 && I1) || (!C1 && T))) 
		|| (!C1 && !I0 && !((C1 && I1) || (!C1 && T)));
	T := Flip_Flop(C1, I1, T);
	assert (C1 && I1 && T) || (C1 && !I1 && !T) || 
		(!C1 && I0 && T) || (!C1 && !I0 && !T);
	assert (C1 || T == I0) && (!C1 || T == I1);
	assert (!C1 ==> T == I0) && (C1 ==> T == I1); 
}

