function fact(n: nat): nat
    requires n >= 1
    decreases n
{
    if n == 1 then 1 else n * fact(n - 1)
}
method ComputeFact(n : nat) returns (res : nat)
    requires n > 0;
    ensures res == fact(n);
{
    res := 1;
    var i := 2;
    while (i <= n)
        invariant res == fact(i - 1) && (i <= n + 1)
        decreases n - i
    {
        res := res * i;
        i := i + 1;
    }
 }

function More(x: int): int {
  if x <= 0 then 1 else More(x - 2) + 3
}
lemma {:induction false} Increasing(x: int)
  ensures x < More(x)
{
  if x <= 0 {
    calc {
      x;
    <=  { assert x <= 0; }
      0;
    <  
      1;
    ==  
      More(x);
    }
  } else {
    calc {
      x < More(x);
    ==  
      x < More(x - 2) + 3;
    ==  
      x - 3 < More(x - 2);
    <==  
      x - 2 < More(x - 2);
    <==  { Increasing(x - 2); }
      true;
    }
  }
}

