method find (a: seq<int>, key: int) returns (index : int)
requires true
ensures key !in a ==> index == -1 
ensures key in a ==> 0 <= index < |a| 
ensures key in a ==> 0 <= index < |a| && a[index] == key
{
    index := 0;
    while (index < |a|) 
        decreases |a| - index
        invariant 0 <= index <= |a| ;
        invariant key !in a[..index];
        {
            if ( a[index] == key ) { 
