function NumbersInTree(t: Tree): multiset<int>
decreases t
{
	match t {
		case Empty => multiset([])
		case Node(n,l,r) => multiset([n])+NumbersInTree(l)+NumbersInTree(r)
	}
}
predicate BST(t: Tree)
decreases t
{
	match t {
		case Empty => true
		case Node(n,l,r) =>
			BST(l) && BST(r) &&
			(forall x :: x in NumbersInTree(l) ==> x < n) &&
			(forall x :: x in NumbersInTree(r) ==> x >= n)
	}
}
method CountOccurrences(t: Tree, key: int) returns (count: nat)
	requires BST(t)
	ensures count == NumbersInTree(t)[key]
  {
    count := CountOccurrences'(t,key);
  }
  method CountOccurrences'(t: Tree, key: int) returns (count: nat)
	requires BST(t)
	ensures count == NumbersInTree(t)[key]
  decreases t, 3
  {
      match t {
        case Empty =>
          count := CountOccurrencesTree1a(t, key);
        case Node(n1, nt1, nt2) =>
          assert t == t && 2 < 3;
          assert nt1 < t;
	        assert nt2 < t;
          count := CountOccurrencesTree1b(t, n1, nt1, nt2, key);
          assert count == NumbersInTree(t)[key];
      }
  }
method CountOccurrencesTree1a(t: Tree, key:int) returns (count: nat)
	requires BST(t)
	requires t == Empty
	ensures count == NumbersInTree(t)[key]
{
	Lemma1a(t, key);
	count := 0;
}
lemma Lemma1a(t: Tree, key:int)
	requires t == Empty
	ensures 0 == NumbersInTree(t)[key]
{}
method CountOccurrencesTree1b(t: Tree, n1: int, nt1: Tree, nt2: Tree, key:int) returns (count: nat)
	requires BST(t)
	requires t == Node(n1, nt1, nt2)
	ensures count == NumbersInTree(t)[key]
	decreases t, 2
{
	assert t == t && 1 < 2;
	count := CountOccurrencesTree2(t, n1, nt1, nt2, key);
}
method CountOccurrencesTree2(t: Tree, n1: int, nt1: Tree, nt2: Tree, key:int) returns (count: nat)
	requires BST(t)
	requires t == Node(n1, nt1, nt2)
	ensures count == NumbersInTree(t)[key]
  decreases t, 1
{
	var tmp1, tmp2;
  assert t == t && 0 < 1;
  assert nt1 < t;
	assert nt2 < t;
  tmp1,tmp2:=0,0;
  if key < n1{
    tmp1 := CountOccurrencesTree3a(t, n1, nt1, nt2, key);
  }
  if n1 == key {tmp1:=tmp1+1;}
  if key >= n1{
    tmp2 := CountOccurrencesTree3b(t, n1, nt1, nt2, tmp1, key);
  }
	count := tmp1+tmp2;
}
method CountOccurrencesTree3a(nt: Tree, n1: int, nt1: Tree, nt2: Tree, key:int) returns (tmp1: nat)
	requires BST(nt)
	requires nt == Node(n1, nt1, nt2)
	ensures tmp1 == NumbersInTree(nt1)[key] && nt == Node(n1, nt1, nt2)
	decreases nt1
{
	assert nt1 < nt; 
	tmp1 := CountOccurrences'(nt1, key);
	Lemma3a(nt, n1, nt1, nt2, tmp1, key);
}
lemma Lemma3a(t: Tree, n1: int, nt1: Tree, nt2: Tree, tmp1: nat, key:int)
	requires tmp1 == NumbersInTree(nt1)[key]
	requires t == Node(n1, nt1, nt2) 
	ensures tmp1 == NumbersInTree(nt1)[key] && t == Node(n1, nt1, nt2)
{}
method CountOccurrencesTree3b(nt: Tree, n1: int, nt1: Tree, nt2: Tree, tmp1: nat, key:int) returns (tmp2: nat)
	requires BST(nt)
	requires tmp1 == NumbersInTree(nt1)[key] + (if n1 == key then 1 else 0) && nt == Node(n1, nt1, nt2)
	ensures tmp1+tmp2 == NumbersInTree(nt)[key]
	decreases nt2
{
	assert nt2 < nt; 
	tmp2 := CountOccurrences'(nt2, key);
	Lemma3b(nt, n1, nt1, nt2, tmp1, tmp2, key);
}
lemma Lemma3b(nt: Tree, n1: int, nt1: Tree, nt2: Tree, tmp1: nat, tmp2: nat, key:int)
	requires tmp2 == NumbersInTree(nt2)[key]
	requires tmp1 == NumbersInTree(nt1)[key] + (if n1 == key then 1 else 0)  && nt == Node(n1, nt1, nt2)
	ensures tmp1+tmp2 == NumbersInTree(nt)[key]
{}
method RecEffective(tree: Tree, key: int) returns (count: nat)
	requires BST(tree)
	decreases tree
	ensures count == NumbersInTree(tree)[key]
  {
  var leftAcc := 0;
  var rightAcc := 0;
	match tree {
	case Empty =>
		assert tree == Empty;
		assert 0 == NumbersInTree(tree)[key];
		count := 0;
		assert count == NumbersInTree(tree)[key];
	case Node(val,left,right) =>
		assert tree == Node(val,left,right);
		assert left < tree;
		assert right < tree;
    if key < val{
      leftAcc := RecEffective(left, key);
    }else{
			rightAcc:= RecEffective(right, key);
    }
		assert (if val == key then 1 else 0) +  leftAcc + rightAcc == NumbersInTree(tree)[key];
    count := (if val == key then 1 else 0) +  leftAcc + rightAcc;
		assert count == NumbersInTree(tree)[key];
	}
	assert count == NumbersInTree(tree)[key];
}

predicate Sorted(a: array<int>, low:int, high:int)
	requires a != null
	requires 0<=low<=high<=a.Length
	reads a
{
	forall j,k:: low<=j<k<high ==> a[j]>=a[k]
}
method sortByScore(bList: array<int>)
	requires bList != null
	requires bList.Length > 1
	ensures Sorted(bList, 0, bList.Length);
	ensures multiset(bList[..]) == multiset(old(bList[..]));
	modifies bList;
{
	var i:=1;
	while (i < bList.Length)
		invariant 1 <= i <= bList.Length;
		invariant Sorted(bList, 0, i);
		invariant multiset(bList[..]) == multiset(old(bList[..]));
	{
		var j := i;
		while (j >= 1 && bList[j-1] < bList[j])
			invariant 0 <= j <= i;
			invariant forall x,y:: (0<=x<y<=i && y!=j) ==> bList[x]>=bList[y];
			invariant multiset(bList[..]) == multiset(old(bList[..]));
		{
			bList[j-1], bList[j] := bList[j], bList[j-1];
			j:=j-1;
		}
		i:=i+1;
	}
}

method FindMax(a: array<int>) returns (idx: int)
  requires a != null && a.Length > 0;
  ensures 0 <= idx < a.Length;
  ensures forall k :: 0 <= k < a.Length ==> a[k] <= a[idx];
{
  var i := 0;
  idx := 0;
  while (i < a.Length)
    invariant 0 <= i <= a.Length;
    invariant 0 <= idx <= i;
    invariant 0 <= idx < a.Length;
    invariant forall k :: 0 <= k < i ==> a[k] <= a[idx];
  {
    if (a[i] > a[idx])
    {
      idx := i;
    }
    i := i + 1;
  }
  return idx;
}

