type Process(==) 
datatype CState = Thinking | Hungry | Eating
class TicketSystem {
    var ticket: int
    var serving: int
    const P: set<Process>
    var cs: map<Process, CState> 
    var t: map<Process, int> 
    predicate Valid() 
        reads this 
    {
        P <= cs.Keys && P <= t.Keys && serving <= ticket && 
        (forall p :: p in P && cs[p] != Thinking ==> serving <= t[p] < ticket) && 
        (forall p,q :: 
            p in P && q in P && p != q && cs[p] != Thinking && cs[q] != Thinking ==> t[p] != t[q] 
        ) && 
        (forall p :: p in P && cs[p] == Eating ==> t[p] == serving) 
    }
    constructor (processes: set<Process>)
        ensures Valid() 
    {
        P := processes;
        ticket, serving := 0,  0;
        cs := map p | p in processes :: Thinking; 
        t := map p | p in processes :: 0;
    }
    method Request(p: Process)
        requires Valid() && p in P && cs[p] == Thinking
        modifies this
        ensures Valid()
    {
        t, ticket := t[p := ticket], ticket + 1; 
        cs := cs[p := Hungry]; 
    }
    method Enter(p: Process)
        requires Valid() && p in P && cs[p] == Hungry
        modifies this
        ensures Valid()
    {
        if t[p] == serving {
            cs := cs[p := Eating]; 
        }
    }
    method Leave(p: Process)
        requires Valid() && p in P && cs[p] == Eating
        modifies this
        ensures Valid()
    {
        assert t[p] == serving;
        serving := serving + 1;
        cs := cs[p := Thinking];
    }
    lemma MutualExclusion(p: Process, q: Process)
        requires Valid() && p in P && q in P 
        requires cs[p] == Eating && cs[q] == Eating 
        ensures p == q 
}

class Acceptor<T(==)>
{
  var promise:  int;
  var accepted: Accepted<T>;
  constructor (value: T)
    modifies this;
    ensures valid();
  {
    promise  := -1;
    accepted := new Accepted(value);
  }
  method Prepare(round: int, value: T) returns (ok: bool, acp: Accepted<T>)
    requires valid();
    modifies this;
  ensures valid() && promise >= round;
  {
    acp := accepted;
    ok  := false;
    if round >= promise && round != accepted.round {
      promise := round;
      assert promise > accepted.round;
      ok := true;
    }
  }
  method Accept(round: int, value: T) returns (ok: bool, acp: Accepted<T>)
    requires valid();
    modifies this, accepted;
  ensures valid() && promise >= round;
  {
    acp := accepted;
    ok  := false;
    if round >= promise {
      promise         := round;
      accepted.round  := round;
      accepted.value  := value;
      ok := true;
    }
  }
  method Get() returns (rnd: int, acp: int, val: T)
    requires valid();
  {
    rnd := promise;
    acp := accepted.round;
    val := accepted.value;
  }
  predicate valid()
    reads this, accepted;
  { accepted != null && promise >= accepted.round }
}
