method InsertionSort( s: seq<int> ) returns ( r: seq<int> )
    ensures multiset(r) == multiset(s);
    ensures IsSorted(r);
{
    r := [];
    var rest := s;
    while rest != []
        decreases rest;
        invariant multiset(s) == multiset(r)+multiset(rest);
        invariant IsSorted(r);
    {
        var x := rest[0];
        assert rest == rest[0..1]+rest[1..];
        rest := rest[1..];
        var k := |r|;
        while k>0 && r[k-1]>x
            invariant 0 <= k <= |r|;
            invariant forall p | k<=p<|r| :: r[p]>x;
        {
            k := k-1;
        }
        assert r == r[..k]+r[k..];
        r := r[..k]+[x]+r[k..];
    }
}

