module ackermann{ 
method Ackermann(m: int, n: int)returns (y:int)
	decreases m, n{
	if m <= 0 {
		y := n + 1;
		return y;
	} else if n <= 0 {
		y := Ackermann(m-1, 1);
		return y;
	} else {
		var x := Ackermann(m, n-1);
		y := Ackermann(m-1, x);
		return y;
	}
}
}

