    constructor (d: int)
      ensures data == d
    { data := d; }
  }
  class Counter ... {
    var c: Cell
    var d: Cell
    ghost predicate Valid...
    {
      this in Repr &&
      c in Repr &&
      d in Repr &&
      c != d &&
      N == c.data - d.data
    }
    constructor Init...
    {
      c := new Cell(0);
      d := new Cell(0);
      new;
      ghost var repr := Repr + {this} + {c,d};
      ...;
      assert ...;
    }
    method Inc...
    {
      ...;
      modify ... {
        c.data := c.data + 1;
      }
      assert ...;
    }
    method Get...
    {
      n := c.data - d.data;
    }
  }
}
