method LinearSearch(a: array<int>, e: int) returns (n:int)
  ensures 0<=n<=a.Length
  ensures n==a.Length || a[n]==e
  ensures n==a.Length ==> forall i::0<=i<a.Length ==> e!=a[i]
{
  var i := 0;
  while i < a.Length
    invariant 0 <= i <= a.Length
    invariant forall j::0<=j<i ==> a[j] != e
  {
    if a[i] == e {
      return i;
    }
    i := i + 1;
  }
  return a.Length;
}

function Factorial(n: int): int
  requires n >= 0
{
  if n == 0 then 1 else n * Factorial(n-1)
}
method LoopyFactorial(n: int) returns (u: int)
  requires n >= 0
  ensures u == Factorial(n)
  {
    u := 1;
    var r := 0;
    while (r < n) 
      invariant u == Factorial(r)
      invariant r <= n
      decreases n-r
    {
      var v := u;
      var s := 1;
      while (s<=r) 
        invariant u == v*s
        invariant s <= r+1
        decreases r-s
      {
        u:=u+v;
        s:=s+1;
      }
      r:=r+1;
      assert u == Factorial(r) && r == s;
    }
  }

module Utils 
{
    method floorSqrt(x: nat) returns (y: nat) 
        ensures x >= y * y && x < (y + 1) * (y + 1)
    {
        if (x == 0 || x == 1) 
        { 
            y := x; 
        }
        var i := 1;
        var res := 1;
        while (res <= x)
            invariant res <= x ==> res == i * i
            invariant res > x ==> x >= (i - 1) * (i - 1) && x < i * i
            decreases  x - res
        {
            i := i + 1;
            res := i * i;
        }
        y := i - 1;
    }
}
module Sudoku 
{
    method isSafe(board: array2<int>, row: int, col: int, num: int) returns (v: bool)
        requires 1 < board.Length0 == board.Length1
        requires 0 <= row < board.Length0 && 0 <= col < board.Length1
    {
        var d := 0;
        var r := 0;
        var len := board.Length0;
        while (d < len)
            invariant 0 <= d <= len;
            decreases  len - d
        {
            if(board[row, d] == num) {   return false;  }
            d := d + 1;
        }
        while (r < len)
            invariant 0 <= r <= len;
            decreases  len - r
        {
            if(board[r, col] == num) {   return false;   }
            r := r + 1;
        }
        var sqrt := Utils.floorSqrt(board.Length0);
        var boxRowStart := row - row % sqrt;
        var boxColStart := col - col % sqrt;
        r := boxRowStart;
        while (r < boxRowStart + sqrt && r < len)
            invariant boxRowStart <= r <= boxRowStart + sqrt
            decreases boxRowStart + sqrt - r
        {   
            d := boxColStart;
            while (d < boxColStart + sqrt && d < len)
                invariant boxColStart <= d <= boxColStart + sqrt
                decreases boxColStart + sqrt - d
            {
                if(board[r, d] == num) {   return false;   }
                d := d + 1;
            }
            r := r + 1;
        }
        return true;
    }
    method solveSudoku(board: array2<int>, numZeros: nat) returns (v: bool) 
        requires 1 < board.Length0 == board.Length1 
        modifies board
        decreases numZeros
    {
        var n := board.Length0;
        var row := -1;
        var col := -1;
        var isEmpty := true;
        var i := 0;
        var j := 0;
        while (i < n)
            decreases n - i
        {
            while (j < n) 
                decreases  n - j
            {
                if (board[i, j] == 0)
                {
                    row := i;
                    col := j;
                    isEmpty := false;
                    break;
                }
                j := j + 1;
            }
            if !isEmpty { break; }
            i := i + 1;
        }
        if (isEmpty || numZeros == 0) {  return true;    }
        var num := 1;
        while (num <= n)
            decreases n - num
        {
            var temp1 := isSafe(board, row, col, num);
            if(temp1) 
            {
                board[row, col] := num;
                var temp2 := solveSudoku(board, numZeros - 1);
                if(temp2) {
                    return true;
                } else {
                    board[row, col] := 0;
                }
            }
            num := num + 1;
        }
        return false;
    }
    method printBoard(board: array2<int>) 
    { 
        var r := 0;
        while r < board.Length0
            decreases board.Length0 - r
        { 
            var d := 0;
            while d < board.Length1
                decreases board.Length1 - d
            { 
                print board[r,d]; 
                print " "; 
                d := d + 1;
            } 
            print "\n" ; 
            r := r + 1; 
        } 
    } 
    method Main() 
    { 
        var board := new int[9, 9];
        var filler := [ 
                [3, 0, 6, 5, 0, 8, 4, 0, 0], 
                [5, 2, 0, 0, 0, 0, 0, 0, 0], 
                [0, 8, 7, 0, 0, 0, 0, 3, 1], 
                [0, 0, 3, 0, 1, 0, 0, 8, 0], 
                [9, 0, 0, 8, 6, 3, 0, 0, 5], 
                [0, 5, 0, 0, 9, 0, 6, 0, 0], 
                [1, 3, 0, 0, 0, 0, 2, 5, 0], 
                [0, 0, 0, 0, 0, 0, 0, 7, 4], 
                [0, 0, 5, 2, 0, 6, 3, 0, 0] 
        ];
        var r := 0;
        while r < board.Length0
            decreases board.Length0 - r
        { 
            var d := 0;
            while d < board.Length1
                decreases board.Length1 - d
            { 
                board[r,d] := filler[r][d];
                d := d + 1;
            } 
            r := r + 1; 
        }
        var numZeros := 0;
        r := 0;
        while r < board.Length0
            decreases board.Length0 - r
        { 
            var d := 0;
            while d < board.Length1
                decreases board.Length1 - d
            { 
                if board[r,d] == 0
                {
                    numZeros := numZeros + 1;
                }
                d := d + 1;
            } 
            r := r + 1; 
        } 
        var solved := solveSudoku(board, numZeros);
        if solved
        { 
            printBoard(board); 
        }  
        else
        { 
            print "No solution"; 
        } 
    } 
}

function sorted(a:array<char>, n:int):bool
    requires 0 <= n <= a.Length
    reads a
{ forall i, j:: (0 <= i < j < n) ==> a[i] <= a[j] }
function partitioned(a:array<char>,i:int,n:int):bool
    requires 0 <= n <= a.Length
    reads a;
{ forall k, l :: 0 <= k < i <= l < n ==> (a[k] <= a[l]) }
method selectSmaller(a:array<char>, i:int, n:int) 
  requires 0 <= i < n <= a.Length
  requires sorted(a, i)
  requires partitioned(a, i, n)
  modifies a
  ensures sorted(a, i+1)
  ensures partitioned(a, i+1, n)
{
  var j := i+1;
  while j < n 
    decreases n - j
    invariant i < j <= n
    invariant a[..i] == old(a[..i])
    invariant forall k :: i < k < j ==> a[i] <= a[k]
    invariant sorted(a, i)
    invariant partitioned(a, i, n)
  {
    if a[j] < a[i]
    { 
      a[i], a[j] := a[j], a[i];
    }
    j := j + 1;
  }
  assert sorted(a, i);
  assert partitioned(a, i, n);
  assert forall k :: i < k < n ==> a[i] <= a[k];
  assert sorted(a, i+1);
  assert partitioned(a, i+1, n);
}
method selectionSort(a:array<char>, n:int)
  requires 0 <= n <= a.Length
  modifies a
{
  var i := 0;
  while i < n 
    decreases n - i
    invariant 0 <= i <= n
    invariant sorted(a, i)
    invariant partitioned(a, i, n)
  {
    selectSmaller(a, i, n);
    assert sorted(a, i+1);
    assert partitioned(a, i+1, n);
    i := i + 1;
  }
}
function sortedRange(a:array<char>, l:int, h:int):bool
    requires 0 <= l <= h <= a.Length
    reads a
{ forall i, j:: (l <= i < j < h) ==> a[i] <= a[j] }

method max(a:array?<int>) returns(max:int) 
  requires a!=null;
  ensures (forall j :int :: (j >= 0 && j < a.Length ==> max >= a[j]));
  ensures (a.Length > 0)==>(exists j : int :: j>=0 && j < a.Length && max==a[j]);
 {
  if (a.Length == 0)  { max := 0;} 
  else {
    max:=a[0];
    var i:int :=1;
    while(i < a.Length)
      invariant (i<=a.Length) && (forall j:int :: j>=0 && j<i ==> max >= a[j])
         && (exists j:int :: j>=0 && j<i && max==a[j]);
      decreases (a.Length-i); 
    {
      if(a[i] > max){max := a[i];}
      i := i + 1;
    }
  }
}
