  ensures fp == fingerPrint && cl == clearance;
  {
    fp := fingerPrint;
    cl := clearance;
  }
  method InvalidateToken()
  modifies this`cl;
  requires this != null;
  ensures cl == -1;
  {
    cl := -1;
  }
}
class EnrollmentStn {
  var fpSet : set<int>;
  method Init()
  modifies this;
  ensures fpSet == {};
  {
    fpSet := {};
  }
  method IssueToken(fingerPrint : int, clearance : int) returns (t : Token)
  modifies this`fpSet;
  requires validClearance(clearance)
  requires fingerPrint !in fpSet;
  ensures fingerPrint in fpSet && t != null;
  ensures fresh(t) && fingerPrint == t.fp && clearance == t.cl;
  ensures fpSet == old(fpSet) + {fingerPrint};
  {
    t := new Token;
    t.Init(fingerPrint, clearance);
    fpSet := fpSet + {fingerPrint};
    return t;
  }
}
class IDStn {
  var clearanceLevel : int;
  var alarm : bool;
  var doorOpen : bool;
  method Init(clearance : int)
  modifies this;
  requires validClearance(clearance);
  ensures clearanceLevel == clearance && alarm == false && doorOpen == false;
  {
    clearanceLevel := clearance;
    alarm := false;
    doorOpen := false;
  }
  method TryOpenDoor(fingerPrint : int, t : Token)
  modifies this`alarm, this`doorOpen, t`cl;
  requires t != null && !alarm && !doorOpen;
  ensures t.fp == fingerPrint && old(t.cl) >= clearanceLevel ==> doorOpen && !alarm;
  ensures t.fp != fingerPrint || old(t.cl) < clearanceLevel ==> !doorOpen && alarm && t.cl == -1;
  {
    if(t.fp == fingerPrint && t.cl >= clearanceLevel){
      doorOpen := true;
      alarm := false;
    } else {
      t.InvalidateToken();
      alarm := true;
      doorOpen := false;
    }
  }
  method CloseDoor()
  modifies this`doorOpen;
  ensures doorOpen == false;
  {
    doorOpen := false;
  }
}
predicate validClearance(clearance : int)
{
   0 <= clearance <= 2
}
method TestMain()
{
  var enrStn := new EnrollmentStn;
  enrStn.Init();
  var idStn := new IDStn;
  idStn.Init(0);
  var t1 := enrStn.IssueToken(1,2);
  idStn.TryOpenDoor(1, t1);
  assert idStn.doorOpen;
  assert !idStn.alarm;
  idStn.CloseDoor();
  assert !idStn.doorOpen;
  var idStn2 := new IDStn;
  idStn2.Init(2);
  var t2 := enrStn.IssueToken(2,1);
  idStn2.TryOpenDoor(2,t2);
  assert !idStn2.doorOpen;
  assert idStn2.alarm;
  assert t2.cl == -1;
  var idStn3 := new IDStn;
  idStn3.Init(1);
  var t3 := enrStn.IssueToken(3,2);
  var t4 := enrStn.IssueToken(4,0);
  idStn3.TryOpenDoor(4,t3);
  assert !idStn3.doorOpen;
  assert idStn3.alarm;
  assert t3.cl == -1;
}

class LimitedStack{
var capacity : int; 
var arr : array<int>; 
var top : int; 
predicate Valid()
reads this, this.arr;
{
      arr != null && capacity > 0 && arr.Length == capacity && top >= -1 && top < capacity  
}
predicate Empty()
reads this;
{
  top == -1
}
predicate Full()
reads this;
{
  top == capacity -1 
}
method Init(c : int)
modifies this;
requires c > 0;
ensures fresh(arr);
ensures arr.Length == c;
ensures Empty();
ensures Valid();
{
capacity := c;
arr := new int[c];
top := -1;
}
method isEmpty() returns (res : bool)
ensures Empty() ==> res;
{
  return top == -1;
}
method Peek() returns (elem : int)
requires this.Valid();
requires !Empty();
ensures elem == arr[top];
{
  return arr[top];
}
method Push(elem : int)
modifies arr, `top;
requires Valid();
requires !Full();
ensures Valid();
ensures top == old(top)+1;
ensures arr[top] == elem;
ensures forall k : int :: 0 <= k < top ==> arr[k] == old(arr[k]);
{
  top := top + 1;
  arr[top] := elem;
}
method Pop() returns (elem : int)
modifies `top;
requires Valid();
requires !Empty();
ensures Valid();
ensures top == old(top)-1;
ensures elem == old(arr[top]);
ensures !Full();
{
  top := top -1;
  return arr[top + 1];
}
method Shift()
requires Valid() && !Empty();
ensures Valid();
ensures forall i : int :: 0 <= i < capacity - 1 ==> arr[i] == old(arr[i + 1]);
ensures top == old(top) - 1;
modifies this.arr, this`top;
{
var i : int := 0;
while (i < capacity - 1 )
invariant 0 <= i < capacity;
invariant top == old(top);
invariant forall j : int :: 0 <= j < i ==> arr[j] == old(arr[j + 1]);
invariant forall j : int :: i <= j < capacity ==> arr[j] == old(arr[j]);
{
arr[i] := arr[i + 1];
i := i + 1;
}
top := top - 1;
}
method Push2(elem : int)
modifies `arr;
      requires Valid();
      requires Full();
      ensures Valid();
      ensures Full();
      ensures arr.Length == old(arr).Length;
      ensures forall k : int :: 0 <= k < capacity - 1 ==> arr[k] == old(arr)[k+1];
      ensures arr[top] == elem;
{
   var temp := new int[capacity];
          forall(i | 0 <= i < arr.Length - 1)
          {
              temp[i] := arr[i+1];
          }
          temp[top] := elem;
          arr := temp;
}
method Main(){
var s := new LimitedStack;
s.Init(3);
assert s.Empty() && !s.Full();
s.Push(27);
assert !s.Empty();
var e := s.Pop();
assert e == 27;
s.Push(5);
s.Push(32);
s.Push(9);
assert s.Full();
var e2 := s.Pop();
assert e2 == 9 && !s.Full();
assert s.arr[0] == 5;
s.Push(e2);
s.Push2(99);
var e3 := s.Peek();
assert e3 == 99;
assert s.arr[0] == 32;
}
}

