predicate isPrefixPred(pre:string, str:string)
{
	(|pre| <= |str|) && 
	pre == str[..|pre|]
}
predicate isNotPrefixPred(pre:string, str:string)
{
	(|pre| > |str|) || 
	pre != str[..|pre|]
}
lemma PrefixNegationLemma(pre:string, str:string)
	ensures  isPrefixPred(pre,str) <==> !isNotPrefixPred(pre,str)
	ensures !isPrefixPred(pre,str) <==>  isNotPrefixPred(pre,str)
{}
method isPrefix(pre: string, str: string) returns (res:bool)
	ensures !res <==> isNotPrefixPred(pre,str)
	ensures  res <==> isPrefixPred(pre,str)
{
    if(|pre| == 0){
        return true;
    }
    if (|pre| > |str|){
        return false;
    }
    res := pre == str[..|pre|];
    return res;
}
predicate isSubstringPred(sub:string, str:string)
{
	(exists i :: 0 <= i <= |str| &&  isPrefixPred(sub, str[i..]))
}
predicate isNotSubstringPred(sub:string, str:string)
{
	(forall i :: 0 <= i <= |str| ==> isNotPrefixPred(sub,str[i..]))
}
lemma SubstringNegationLemma(sub:string, str:string)
	ensures  isSubstringPred(sub,str) <==> !isNotSubstringPred(sub,str)
	ensures !isSubstringPred(sub,str) <==>  isNotSubstringPred(sub,str)
{}
method isSubstring(sub: string, str: string) returns (res:bool)
    ensures  res <==> isSubstringPred(sub, str)
	ensures !res <==> isNotSubstringPred(sub, str) 
{
    res := false;
    if (|str| < |sub|){
        return res;
    }
    assert(|str| >= |sub|);
    var i:= 0;
    var substr: string;
    while (i <= |str| && res == false)
        decreases |str| - i
        invariant 0 <= i < |str| + 2 && ((forall j :: 0 <= j < i ==> isNotPrefixPred(sub, str[j..])) <==> res == false) 
    {
        substr := str[i..];
        res := isPrefix(sub, substr);
        i := i + 1; 
    }
    return res;
}
predicate haveCommonKSubstringPred(k:nat, str1:string, str2:string)
{
	exists i1, j1 :: 0 <= i1 <= |str1|- k && j1 == i1 + k && isSubstringPred(str1[i1..j1],str2)
}
predicate haveNotCommonKSubstringPred(k:nat, str1:string, str2:string)
{
	forall i1, j1 :: 0 <= i1 <= |str1|- k && j1 == i1 + k ==>  isNotSubstringPred(str1[i1..j1],str2)
}
lemma commonKSubstringLemma(k:nat, str1:string, str2:string)
	ensures  haveCommonKSubstringPred(k,str1,str2) <==> !haveNotCommonKSubstringPred(k,str1,str2)
	ensures !haveCommonKSubstringPred(k,str1,str2) <==>  haveNotCommonKSubstringPred(k,str1,str2)
{}
method haveCommonKSubstring(k: nat, str1: string, str2: string) returns (found: bool)
	ensures found  <==> haveCommonKSubstringPred(k,str1,str2)
    ensures !found <==> haveNotCommonKSubstringPred(k,str1,str2) 
{
    if (k > |str1| || k > |str2|){
        return false;
    }
    var i := 0;
    found := false;
    while(i <= |str1| - k && found == false)
        decreases |str1| - i 
        invariant 0 <= k <= |str1|
        invariant 0 <= i <= |str1| - k + 1  && (found == false <==> forall j, l :: 0 <= j < i && l == j + k ==> isNotSubstringPred(str1[j..l], str2))
    {
        var substr : string := str1[i..i+k];
        found := isSubstring(substr, str2);
        i := i + 1;
    }
    return found;
}
method maxCommonSubstringLength(str1: string, str2: string) returns (len:nat)
	requires (|str1| <= |str2|)
	ensures (forall k :: len < k <= |str1| ==> !haveCommonKSubstringPred(k,str1,str2))
	ensures haveCommonKSubstringPred(len,str1,str2)
{
    assert(|str1| <= |str2|);
    assert(0 <= |str1|);
    assert(0 <= |str2|);
    len := |str1|;
    var res : bool := true;
    while(len > 0)
        decreases |str1| - (|str1| - len)
        invariant 0 <= len <= |str1| + 1
        invariant forall k :: len < k <= |str1| ==> !haveCommonKSubstringPred(k,str1,str2)
    {
        res := haveCommonKSubstring(len, str1, str2);
        if(res){
            break;
        }
       len := len - 1;
    }
    assert isPrefixPred(str1[|str1|..|str1|], str2[0..]);
    return len;
}

datatype Status = Shelf | Patron(name: string)
datatype Book = Book(title: string)
datatype Variables = Variables(library: map<Book, Status>)
{
  ghost predicate WellFormed()
  {
    forall b: Book :: b.title == "" ==> b !in this.library
  }
}
ghost predicate Init(v: Variables)
{
  && v.WellFormed()
  && forall b :: b in v.library ==> v.library[b].Shelf?
}
datatype Step = Checkout(b: Book, to: string) | Return(b: Book)
ghost predicate CheckoutStep(v: Variables, v': Variables, step: Step)
  requires step.Checkout?
{
  && v.WellFormed()
  && step.b in v.library
  && v.library[step.b].Shelf?
  && v' == v.(library := v.library[step.b := Patron(step.to)])
}
ghost predicate ReturnStep(v: Variables, v': Variables, step: Step)
  requires step.Return?
{
  && v.WellFormed()
  && step.b in v.library
  && v.library[step.b].Patron?
  && v' == v.(library := v.library[step.b := Shelf])
}
ghost predicate NextStep(v: Variables, v': Variables, step: Step)
{
  match step {
    case Checkout(_, _) => CheckoutStep(v, v', step)
    case Return(_) => ReturnStep(v, v', step)
  }
}
ghost predicate Next(v: Variables, v': Variables)
{
  exists step :: NextStep(v, v', step)
}
lemma NextStepDeterministicGivenStep(v:Variables, v':Variables, step: Step)
  requires NextStep(v, v', step)
  ensures forall v'' | NextStep(v, v'', step) :: v' == v''
{}
lemma ExampleExec() {
  var e := [
    Variables(library := map[Book("Snow Crash") := Shelf, Book("The Stand") := Shelf]),
    Variables(library := map[Book("Snow Crash") := Patron("Jon"), Book("The Stand") := Shelf]),
    Variables(library := map[Book("Snow Crash") := Patron("Jon"), Book("The Stand") := Patron("Tej")]),
    Variables(library := map[Book("Snow Crash") := Shelf, Book("The Stand") := Patron("Tej")])
  ];
  assert Init(e[0]);
  var steps := [
    Checkout(Book("Snow Crash"), "Jon"),
    Checkout(Book("The Stand"), "Tej"),
    Return(Book("Snow Crash"))
  ];
  assert forall n: nat | n < |e|-1  :: NextStep(e[n], e[n+1], steps[n]);
  assert forall n: nat | n < |e|-1  :: Next(e[n], e[n+1]);
}

method insertionSort(a : array<nat>)
    modifies a
{
    var i := 0;
    while (i < a.Length)
    {
        var j := i - 1;
        while (j >= 0 && a[j] > a[j + 1])
        {
            a[j], a[j + 1] := a[j + 1], a[j];
            j := j - 1;
        }
        i := i + 1;
    }
}
datatype InputSpace = InputSpace(a:array<nat>)
datatype StateSpace = StateSpace(a:array<nat>, idx:nat)
predicate sorted(state: StateSpace, begin: int, end: int)
    reads state.a
    requires state.a.Length > 0
{
    forall i, j :: 0 <= begin <= i <= j <= end < state.a.Length ==> state.a[i] <= state.a[j]
}
method insertionSortTransitionSystem(initialState : StateSpace) returns (terminalState:StateSpace)
    modifies initialState.a
    requires initialState.a.Length > 0
    ensures terminalState.a.Length > 0
    ensures multiset(initialState.a[..]) == multiset(terminalState.a[..])
    ensures sorted(terminalState, 0, terminalState.a.Length - 1)
{
    var i := 0;
    while (i < initialState.a.Length)
        decreases initialState.a.Length - i
        invariant 0 <= i <= initialState.a.Length
        invariant sorted(initialState, 0, i - 1)
    {
        var j := i - 1;
        while (j >= 0 && initialState.a[j] > initialState.a[j + 1])
            invariant -1 <= j <= i - 1
            invariant forall k :: j + 1 < k <= i ==> initialState.a[j + 1] < initialState.a[k]
            invariant forall m, n :: 0 <= m <= j && j + 2 <= n <= i ==> initialState.a[m] <= initialState.a[n]
            invariant sorted(initialState, 0, j)
            invariant sorted(initialState, j + 2, i)
            decreases j
        {
            initialState.a[j], initialState.a[j + 1] := initialState.a[j + 1], initialState.a[j];
            j := j - 1;
        }
        i := i + 1;
    }
    assert sorted(initialState, 0, initialState.a.Length - 1);
    terminalState := initialState;
}
