predicate isSorted(a: array<int>)
    reads a
{
    forall i, j :: 0 <= i < j < a.Length ==> a[i] <= a[j]
}
method insertionSort(a: array<int>)
    modifies a
    ensures isSorted(a)
    ensures multiset(a[..]) == multiset(old(a[..]))
{
    var i := 0;
    while i < a.Length 
        decreases a.Length - i
        invariant 0 <= i <= a.Length
        invariant a.Length == 0 || 0 <= i <= a.Length
        invariant forall l, r :: 0 <= l < r < i <= a.Length ==> a[l] <= a[r]
        invariant multiset(a[..]) == multiset(old(a[..]))
    {
        var j := i;
        while j > 0 && a[j-1] > a[j]
            decreases j
            invariant forall l, r :: (0 <= l < r <= i  && j != r) ==> a[l] <= a[r]
            invariant multiset(a[..]) == multiset(old(a[..]))
        {
            a[j-1], a[j] := a[j], a[j-1];
            j := j - 1;
        }
        i := i + 1;
    }
}

module Libraries__ModField {
newtype modulo = x : nat | 0 < x witness 1
datatype ModField = ModField(
    val: int,
    mod: modulo
)
predicate members_of_same_field(m: ModField, n: ModField) {
    m.mod == n.mod  
}
function canonical(m: ModField) : ModField {
    ModField(m.val % m.mod as nat, m.mod)
}
predicate congruent(m: ModField, n: ModField) {
    members_of_same_field(m, n)
    && canonical(m) == canonical(n)
}
function add(m: ModField, n: ModField) : ModField 
    requires members_of_same_field(m, n);
{
    assert m.mod == n.mod;
    ModField(m.val + n.val, m.mod)
}
function additiveInverse(m: ModField) : ModField 
{
    ModField(-m.val, m.mod)
}
}

datatype Colour = RED | WHITE | BLUE
method FlagSort(flag: array?<Colour>) returns (white: int, blue: int)
requires flag!=null
ensures 0 <= white<=blue <= flag.Length
ensures forall i:: 0     <=i <white       ==> flag[i]==RED
ensures forall i:: white <=i <blue        ==> flag[i]==WHITE
ensures forall i:: blue  <=i <flag.Length ==> flag[i]==BLUE
ensures multiset(flag[..])== multiset(old(flag[..]))
modifies flag;
{
    var next := 0;
    white := 0;
    blue := flag.Length;    
    while (next != blue)    
    invariant 0 <= white<=next<=blue <= flag.Length
    invariant forall i:: 0    <=i<white       ==> flag[i]==RED
    invariant forall i:: white<=i<next        ==> flag[i]==WHITE
    invariant forall i:: blue <=i<flag.Length ==> flag[i]==BLUE
    invariant multiset(flag[..])== multiset(old(flag[..]))
    {
            match (flag[next])
            {
            case WHITE => next:=next+1;
            case BLUE  => blue:=blue-1;
                          flag[next], flag[blue] := flag[blue], flag[next];
            case RED   => flag[next], flag[white] := flag[white], flag[next];
                          next:=next+1;
                          white:=white+1;
            }
    }
}
