function PowersOfTwo(d: nat) : nat { if d == 0 then 1 else 2*PowersOfTwo(d-1) }
method FindLog(N: nat) returns (l: nat, n: nat)
	requires 1 <= N
	ensures PowersOfTwo(l) <= N < PowersOfTwo(l+1)
{
	n := N;
	assert 1 <= N == n;
	assert  1 <= N;
	l,n := FL1(N,n);
	assert Inv(l,n,N);
	L1(N,l,n);
	assert PowersOfTwo(l) <= N < PowersOfTwo(l+1);
}
method FL1(N: nat ,n0: nat) returns (l: nat, n: nat)
	requires 1 <= N
	requires n0 == N
	ensures Inv(l,n,N)
{
	n := n0;
	assert n==N && 1 <= N && 0==0;
	l := 0;
	assert l == 0;
	l,n := FL2(N,n,l);
	assert Inv(l,n,N);
}
method FL2(N: nat ,n0: nat, l0: nat) returns (l: nat, n: nat)
	requires 1 <= N
	requires n0 == N
	requires l0 == 0
	ensures Inv(l,n,N)
{
	l := l0;
	n := n0;
	assert 1<=N && n == N == n0 && l == 0 == l0;
	l,n := FL22(N,n,l);
	assert Inv(l,n,N) && n==1;
}
method FL22(N: nat ,n0: nat, l0: nat) returns (l: nat, n: nat)
	requires 1 <= N
	requires n0 == N
	requires l0 == 0
	ensures Inv(l,n,N) && n==1
{
	l,n := l0,n0;
	assert 1 <=N && n==N && l == 0;
	assert Inv(l,n,N);
	while(2 <= n )
		invariant Inv(l,n,N)
		decreases n
	{
		ghost var n1: nat := n;
		ghost var l1: nat := l;
		assert Inv(l,n,N) && 2 <= n == n1 <= N && l1 == l;
		l,n := FL3(N,n,l);
		assert  Inv(l,n,N) && 0 < n == n1/2 && 0 < l==l1+1;
	}
	assert n == 1 && Inv(l,n,N);
}
 method FL3(N: nat ,n0: nat, l0: nat) returns (l: nat, n: nat)
	requires Inv(l0,n0,N) 
	requires 1 < N
	requires 2 <= n0 <= N
	ensures Inv(l,n,N) && 0 < n == n0/2 && 0 < l == l0+1
{
	l,n := l0,n0;
	assert l0 == l;
	assert 2 <= n == n0;
	assert Inv(l,n,N) && 1 < N ;
	l,n := FL4(N,n,l);
	assert Inv(l+1,n/2,N) && l == l0 && n == n0;
	l:= l0 +1;
	n:= n0/2;
	assert  Inv(l,n,N) && 0 < n == n0/2 && 0 < l==l0+1 ;
}
	method FL4(N: nat ,n0: nat, l0: nat) returns (l: nat, n: nat)
	requires Inv(l0,n0,N) && 1 < N && 2 <= n0 
	ensures Inv(l+1,n/2,N) && l0==l && n0==n
{
	l,n := l0,n0;
	assert l0 == l;
	assert n0 == n;
	assert 2 <= n;
	assert Inv(l,n,N) && 1 < N;
	L4(N,l,n);
	assert Inv(l+1,n/2,N) && l0==l && n0==n;
}
lemma L4(N: nat ,l: nat, n: nat)
	requires  1 < N 
	requires n>=2 
	requires Inv(l,n,N) 
	ensures  Inv(l+1,n/2,N) 
lemma L1(N: nat ,l: nat, n: nat)
	requires 1 <= N
	requires Inv(l,n,N)
	ensures PowersOfTwo(l) <= N < PowersOfTwo(l+1)
predicate Inv(l: nat, n: nat, N: nat)
{
	n * PowersOfTwo(l) <= N < (n + 1) * PowersOfTwo(l) && 1 <= n
}

predicate hasPrefix(token: string, str: string)
{
   (|token| == 0) || ((|token| <= |str|) && (token == str[..|token|]))
}
predicate hasNoPrefix(token:string, str:string)
{
	(|token| > |str|) || (token != str[..|token|])
}
predicate hasSubstring(token:string, str:string)
{
	(exists n : int :: (0 <= n < |str| + 1) && hasPrefix(token, str[n..]))
}
predicate hasNoSubstring(token:string, str:string)
{
	(forall n : int | (0 <= n < |str| + 1) :: hasNoPrefix(token,str[n..]))
}
method isPrefix(token: string, str: string) returns (r:bool)
   requires |token| >= 0 && |str| >= 0
	ensures (r == true) <==> hasPrefix(token,str)
	ensures (r == false) <==> hasNoPrefix(token,str)
{
	if (|token| == 0)
	{
		r:=true;
	}
   if (|str| < |token|)
	{
		r:=false;
	}
   else 
   {
      r:=(str[..|token|] == token);
   }
}
method isSubstring(token: string, str: string) returns (r:bool)
   requires |token| >= 0
   requires |str| >= 0
   requires |token| <= |str|
	ensures (r == true) ==> (|token| == 0) || hasSubstring(token, str)
	ensures (r == false) <==> hasNoSubstring(token, str)
{
   var m;
	var i := 0;
	r := false;
	while ((i < (|str| + 1)) && (r == false))
		invariant 0 <= i <= |str|+1
		invariant (r == true) ==> hasSubstring(token, str)
		invariant (r == false) <==> (forall l :: 0 <= l < i ==> hasNoPrefix(token, str[l..]))
		decreases |str| - i
	{
		m := isPrefix(token, str[i..]);
		if (m == true) {
			r := true;
		}
		i := i + 1;
	}
}
