method isSubstring(sub: string, str: string) returns (res:bool)
    requires |sub| <= |str|
    requires 0 < |sub|
    requires 0 < |str|
{
    var i := 0;
    var j := |str|-|sub|;
    var temp : bool;
    while i <= j
        invariant i >= 0
        decreases j - i
    {
        temp := isPrefix(sub, str[i..]);
        if temp
            { return true; }
        else
            { i := i+1; }
    }
    return false;
}
method haveCommonKSubstring(k: nat, str1: string, str2: string) returns (found: bool)
    requires |str1| > 0
    requires |str2| > 0
    requires k > 0
    requires k <= |str1|
    requires k <= |str2|
{
   var i := 0;
   var endIndex := |str1| - k;
   var temp : bool;
    while i < endIndex
        invariant i >= 0
        decreases endIndex - i
    {
        temp := isSubstring(str1[i..i+k], str2);
        if temp
            { return true; }
        else
            { i := i+1; }
    }
    return false;
}
method maxCommonSubstringLength(str1: string, str2: string) returns (len:nat)
    requires |str1| > 0
    requires |str2| > 0
{
