    while(i <= |str1| && i <= |str2|)
        decreases |str1| - i
    {
        hasCommonSubstring := haveCommonKSubstring(i, str1, str2);
        if hasCommonSubstring == false
            { return commonSubstrLength; }
        else 
            { i := i +1; }
    }
    return commonSubstrLength;
}

