    while(i < a.Length)
        invariant 0 <= i <= a.Length;
        invariant partOrdered(a,0,i);
        invariant forall k, l :: 0 <= k < i <= l < a.Length ==> a[l] <= a[k];
        invariant permutation (a[..], old(a[..]));
    {
            var t;
            var m := findMaxIndex(a,i);
            assert forall k :: i <= k < a.Length ==> a[k] <= a[m];
            t := a[i];
            a[i] := a[m];
            a[m] := t;
            i := i+1;
    }
