method add_by_inc_vc(n: nat, m: nat) returns (r: nat)
  ensures r == m + n;
{
  assume (true); 
  r := n;
  var i := 0;
  assert (i <= m && r == n + i); 
  i, r := *, *;
  assume (i <= m && r == n + i);
  if ( i < m )
  {
    ghost var rank0 := m - i;
    i := i + 1;
    r := r + 1;
    assert(i <= m && r == n + i);
    assert(m - i < rank0);
    ghost var rank1 := m - i;
    assert(rank1 < rank0);
    assert(rank1 >= 0);
    assume(false);
  }
  assert(r == m + n);
  return r;
}

