method IncDec(x: int, y: int) returns (sum: int)
ensures sum == x+y;
{
    sum := 0;
    var k, j:= x, y;
    while k!= 0 || j!=0
    invariant x + y == sum + k + j
    decreases Abs(k)
    decreases Abs(j)
    {
        if k < 0{
            sum:=sum-1;
            k:=k+1;
        }
        else if k > 0{
            sum:=sum+1;
            k:=k-1;
        }
        if j < 0{
            sum:=sum-1;
            j:=j+1;
        }
        else if j > 0{
            sum:=sum+1;
            j:=j-1;
        }
    }
}
