method hasEnoughVolume(blood: seq<Blood>, volume: int) returns (b: bool)
requires forall x :: 0 <= x < |blood| ==> blood[x] != null
{   
    b := false;
    var i : int;
    i := 0;
    var totalVolume: int;
    totalVolume := 0;
    while i < |blood|
    decreases |blood| - i
    invariant 0 <= i <= |blood|
    {
        totalVolume := totalVolume + blood[i].volume;
        i := i + 1;
    }
    if totalVolume > volume {
        b:= true;
    }
}
