        modifies this
        ensures this.state != old(this.state)
        ensures currentState == this.state
    {
        this.state := ! this.state;
        currentState:= this.state;
    }
    method getState() returns (currentState : bool)
        ensures currentState == this.state
        ensures this.state ==  old(this.state)
    {
        currentState := this.state;
    }
}
