method preserveSumProduct(x: int, y:int) returns (p: int, q: int)
ensures p + q == x + y && p*q < 0
{
    p := x-y*y-1;
    assert p == x-y*y-1;
    q := y+y*y+1;
    assert p == x-y*y-1 && q == y+y*y+1 && p+q == x + y;
    if (x > y) {
        assert x > y && p == x-y*y-1 && q == y+y*y+1;
        p := x+x*x+1;
        q := y-x*x-1;
        assert x > y && p == x+x*x+1 && q == y-x*x-1;
        assert x > y && p-x-1 == x*x && q == y-x*x-1;
        assert x > y && x <= p-x-1 && q == y-x*x-1;
        assert x > y && 1 <= p && q == y-x*x-1;
        assert x > y && p > 0 && q == y-x*x-1;
        assert x*x > y && p > 0 && q == y-x*x-1;
        assert x*x > y && p > 0 && x*x == y-q-1;
        assert p > 0 && y-q-1 > y;
        assert p > 0 && -q-1 > 0;
        assert p > 0 && q < -1;
        assert p > 0 && q < 0;
        assert p*q < 0;
    }
}

predicate acheck(a: array<int>, n: int)
reads a
requires n >= 1
{
	a.Length % 2 == 0 && 
	forall i :: 0 <= i < a.Length ==> 
		if i % n == 0 then a[i] == 0 else a[i] != 0
}
method Main()
{
	var arr: array<int> := new int[][0,42,0,42];
	var res := acheck(arr, 2);
	assert res;
	arr := new int[][];
	res := acheck(arr, 2);
	assert res;
	arr := new int[][0,4,2,0];
	assert arr[2] == 2;
	res := acheck(arr, 2);
