            invariant 0 <= lo <= hi <= a.Length
            invariant key !in a[..lo] && key !in a[hi..]
        {
            var mid := (lo + hi) / 2;
            if key < a[mid] {
            hi := mid;
            } else if a[mid] < key {
            lo := mid + 1;
            } else {
            return mid;
            }
        }
        return -1;
        }
