predicate isPrefixPred(pre:string, str:string)
{
	(|pre| <= |str|) && 
	pre == str[..|pre|]
}
predicate isNotPrefixPred(pre:string, str:string)
{
	(|pre| > |str|) || 
	pre != str[..|pre|]
}
lemma PrefixNegationLemma(pre:string, str:string)
	ensures  isPrefixPred(pre,str) <==> !isNotPrefixPred(pre,str)
	ensures !isPrefixPred(pre,str) <==>  isNotPrefixPred(pre,str)
{}
method isPrefix(pre: string, str: string) returns (res:bool)
	ensures !res <==> isNotPrefixPred(pre,str)
	ensures  res <==> isPrefixPred(pre,str)
{
	if |pre| > |str|
    	{return false;}
  	var i := 0;
  	while i < |pre|
    	decreases |pre| - i
    	invariant 0 <= i <= |pre|
    	invariant forall j :: 0 <= j < i ==> pre[j] == str[j]
  	{
    	if pre[i] != str[i]
    	{
       		return false;
    	} 
    	i := i + 1;
  	}
 	return true;
}
predicate isSubstringPred(sub:string, str:string)
{
	(exists i :: 0 <= i <= |str| &&  isPrefixPred(sub, str[i..]))
}
predicate isNotSubstringPred(sub:string, str:string)
{
	(forall i :: 0 <= i <= |str| ==> isNotPrefixPred(sub,str[i..]))
}
lemma SubstringNegationLemma(sub:string, str:string)
	ensures  isSubstringPred(sub,str) <==> !isNotSubstringPred(sub,str)
	ensures !isSubstringPred(sub,str) <==>  isNotSubstringPred(sub,str)
{}
method isSubstring(sub: string, str: string) returns (res:bool)
	ensures  res <==> isSubstringPred(sub, str)
{
	if |sub| > |str| {
        return false;
    }
    var i := |str| - |sub|;
    while i >= 0 
    decreases i
    invariant i >= -1
    invariant forall j :: i <  j <= |str|-|sub| ==> !(isPrefixPred(sub, str[j..]))
    {
        var isPref := isPrefix(sub, str[i..]);
        if isPref
        {
            return true;
        }
        i := i-1;
    }
    return false;
}
predicate haveCommonKSubstringPred(k:nat, str1:string, str2:string)
{
	exists i1, j1 :: 0 <= i1 <= |str1|- k && j1 == i1 + k && isSubstringPred(str1[i1..j1],str2)
}
predicate haveNotCommonKSubstringPred(k:nat, str1:string, str2:string)
{
	forall i1, j1 :: 0 <= i1 <= |str1|- k && j1 == i1 + k ==>  isNotSubstringPred(str1[i1..j1],str2)
}
lemma commonKSubstringLemma(k:nat, str1:string, str2:string)
	ensures  haveCommonKSubstringPred(k,str1,str2) <==> !haveNotCommonKSubstringPred(k,str1,str2)
	ensures !haveCommonKSubstringPred(k,str1,str2) <==>  haveNotCommonKSubstringPred(k,str1,str2)
{}
method haveCommonKSubstring(k: nat, str1: string, str2: string) returns (found: bool)
	ensures found  <==>  haveCommonKSubstringPred(k,str1,str2)
{
	 if( |str1| < k || |str2| < k){
        return false;
    }
    var i := |str1| - k;
    while i >= 0
      decreases i
      invariant i >= -1 
      invariant forall j,t :: i < j <= |str1| - k && t==j+k ==> !isSubstringPred(str1[j..t], str2)
    {
				var t := i+k;
        var isSub := isSubstring(str1[i..t], str2);
        if isSub 
        {
            return true;
        }
        i := i-1;
    }
    return false;
}
method maxCommonSubstringLength(str1: string, str2: string) returns (len:nat)
	requires (|str1| <= |str2|)
	ensures (forall k :: len < k <= |str1| ==> !haveCommonKSubstringPred(k,str1,str2))
	ensures haveCommonKSubstringPred(len,str1,str2)
{
	var i := |str1|;
  	while i > 0
  	decreases i
  	invariant i >= 0
  	invariant forall j :: i < j <= |str1| ==> !haveCommonKSubstringPred(j, str1, str2)
  	{
    	var ans := haveCommonKSubstring(i, str1, str2);
    	if ans {
       		return i;
    	}
    	i := i -1;
  	}
  	assert i == 0;
	assert isPrefixPred(str1[0..0],str2[0..]);
  	return 0;
}

method Product1 (m: nat, n: nat) returns (res:nat)
     ensures res == m * n;
 {
     var m1: nat := 0;
     var n1: nat := 0;
     res := 0;
     while (m1 < m)
       invariant m>=m1>=0
       invariant res == m1*n
     {
         n1 := 0;
         while (n1 < n)
           invariant n>=n1>=0
           invariant res == m1*n+n1
         {
             res := res + 1;
             n1 := n1 + 1;
         }
         m1 := m1 + 1;
     }
  }

class Node
{
    var next: seq<Node>
}
predicate closed(graph: set<Node>)
    reads graph
{
    forall i :: i in graph ==>
        forall k :: 0 <= k < |i.next| ==> i.next[k] in graph && i.next[k] != i
}
predicate pathSpecific(p: seq<Node>, start: Node, end: Node, graph: set<Node>)
    requires closed(graph)
    reads graph
{
    |p| > 0 && 
    p[0] == start && p[|p|-1] == end && 
    path(p, graph) 
}
predicate path(p: seq<Node>, graph: set<Node>)
    requires closed(graph)
    requires |p| > 0
    reads graph
{
    p[0] in graph &&
    (|p| > 1 ==> p[1] in p[0].next && 
        path(p[1..], graph)) 
}
lemma ClosedLemma(subgraph: set<Node>, root: Node, goal: Node, graph: set<Node>)
    requires closed(subgraph)
    requires closed(graph)
    requires subgraph <= graph
    requires root in subgraph
    requires goal in graph - subgraph
    ensures !(exists p: seq<Node> :: pathSpecific(p, root, goal, graph))
{
    forall p {
        DisproofLemma(p, subgraph, root, goal, graph);
    }
}
lemma DisproofLemma(p: seq<Node>, subgraph: set<Node>, root: Node, goal: Node, graph: set<Node>)
    requires closed(subgraph)
    requires closed(graph)
    requires subgraph <= graph
    requires root in subgraph
    requires goal in graph - subgraph
    ensures !pathSpecific(p, root, goal, graph)
{
    if |p| > 1 && p[0] == root && p[|p|-1] == goal {
        if p[1] in p[0].next {
            DisproofLemma(p[1..], subgraph, p[1], goal, graph);
        }
    }
}

function factorial (n :nat) : nat
  requires n > 0;
{
  if (n == 1) then 1
  else n * factorial(n-1)
}
method Factorial (n: nat) returns (v:nat)
  requires n > 0;
  ensures v == factorial(n);
{
  v := 1;
  if (n == 1) { return v; }
  var i := 2;
  while (i <= n)
    invariant i <= n+1;
    invariant v == factorial(i-1);
  {
    v := i * v;
    i := i + 1;
  }
  return v;
}
method FactorialTuring(n: nat) returns (v: nat)
  requires n > 0;
  ensures v == factorial(n);
{
  var r := 1;
  var u := 1;
  while (true)
    decreases n - r;
    invariant u == factorial(r);
    invariant r <= n;
  {
    v := u;
    if (r - n >= 0)
    { assert (r == n); return v; }
    var s := 1;
    while (true)
      decreases r + 1 - s
      invariant s <= r < n
      invariant u == s * factorial(r)
    {
      u := u + v;
      s := s + 1;
      if ((s - (r + 1)) >= 0)
      {
        assert (s == r+1);
        break;
      }
    }
    r := r + 1;
  }
}

method findScarce(a:seq<array<int>>, limit: int) returns (scarce: seq<array<int>>)
  requires a != [] && |a| > 0;
  requires forall k :: 0 <= k < |a| ==> a[k] != null;
  requires forall k :: 0 <= k < |a| ==> a[k].Length == 2;
  ensures forall k: int :: (0 <= k < |a| ==> ((a[k][1] <= limit) ==> a[k] in scarce));
  ensures forall k: int :: (0 <= k < |scarce| ==> (scarce[k] != null && scarce[k] in multiset(a[..])));
{
  var i : int := 0;
  scarce := [];
  while (i < |a|)
    invariant 0 <= i <= |a|;
    invariant forall k: int :: (0 <= k < i ==> ((a[k][1] <= limit) ==> a[k] in scarce));
    invariant forall k: int :: (0 <= k < |scarce| ==> (scarce[k] != null && scarce[k] in multiset(a[..])));
  {
    if (a[i][1] <= limit) 
    { 
      scarce := scarce + [a[i]];
    }
    i := i + 1;
  }
}
