    (i,j) requires 0 <= i < matrix.Length1 && 0 <= j < matrix.Length0 reads matrix => matrix[j,i]);
  var m0, m1, m2 := Transpose(matrix);
  PrintMatrix(m');
  PrintMatrix(m0);
  PrintMatrix(m1);
