class Node {
  var Repr: set<Node>  
  ghost predicate Valid()
    reads this, Repr
  {
    this in Repr &&
    (next != null ==>
      next in Repr && next.Repr <= Repr && this !in next.Repr && next.Valid())
  }
  var val: int
  var next: Node?
  constructor (val: int, next: Node?)
    requires next != null ==> next.Valid()
    ensures Valid() && fresh(Repr - if next == null then {} else next.Repr)
  {
    this.val, this.next := val, next;
    Repr := {this} + if next == null then {} else next.Repr;
  }
  static method Create(n: nat) returns (d: Node)
    ensures d.Valid() && fresh(d.Repr)
  {
    d := new Node(0, null);
    var i := 1;
    while i < n
      invariant d.Valid() && fresh(d.Repr)
    {
      d := new Node(i, d);
      i := i + 1;
    }
  }
  method Print()
    requires Valid()
  {
    var d := this;
    while d != null
      invariant d != null ==> d.Valid()
      decreases if d == null then {} else d.Repr
    {
      print d.val, " ";
      d := d.next;
    }
    print "\n";
  }
  predicate StartsWith(that: Node?)
    requires Valid() && (that != null ==> that.Valid())
    reads Repr, if that == null then {} else that.Repr
    decreases Repr
  {
    that != null ==>
      val == that.val && next != null && next.StartsWith(that.next)
  }
  method IncEverything(n: int)
    requires Valid()
    modifies Repr
    ensures Valid()
  {
    forall d | d in Repr {
      d.val := d.val + n;
    }
    StillValidAfterValChanges();
  }
  twostate lemma StillValidAfterValChanges()
    requires old(Valid())
    requires forall d :: d in old(Repr) ==> unchanged(d`next, d`Repr)
    ensures Valid()
    decreases old(Repr)
  {
    if next != null {
      next.StillValidAfterValChanges();
    }
  }
}

lemma SkippingLemma(a : array?<int>, j : int)
   requires a != null
   requires forall i :: 0 <= i < a.Length ==> 0 <= a[i]
   requires forall i :: 0 < i < a.Length ==> a[i-1]-1 <= a[i]
   requires 0 <= j < a.Length
   ensures forall i :: j <= i < j + a[j] && i < a.Length ==> a[i] != 0
{
   var i := j;
   while i < j + a[j] && i < a.Length
      decreases j + a[j] - i
      invariant i < a.Length ==> a[j] - (i-j) <= a[i]
      invariant forall k :: j <= k < i && k < a.Length ==> a[k] != 0
   {
      i := i + 1;
   }
}
method FindZero(a: array?<int>) returns (index: int)
   requires a != null
   requires forall i :: 0 <= i < a.Length ==> 0 <= a[i]
   requires forall i :: 0 < i < a.Length ==> a[i-1]-1 <= a[i]
   ensures index < 0  ==> forall i :: 0 <= i < a.Length ==> a[i] != 0
   ensures 0 <= index ==> index < a.Length && a[index] == 0
{
   index := 0;
   while index < a.Length
      decreases a.Length - index
      invariant 0 <= index
      invariant forall k :: 0 <= k < index && k < a.Length ==> a[k] != 0
   {
      if a[index] == 0 { return; }
      SkippingLemma(a, index);
      index := index + a[index];
   }
   index := -1;
}
lemma DistributiveLemma(a: seq<bool>, b: seq<bool>)
   decreases a, b
   ensures count(a + b) == count(a) + count(b)
{
   if a == []
   {
      assert a + b == b;
   }
   else
   {
      DistributiveLemma(a[1..], b);
      assert a + b == [a[0]] + (a[1..] + b);
   }
}
function count(a: seq<bool>): nat
   decreases a
{
   if |a| == 0 then 0 else
   (if a[0] then 1 else 0) + count(a[1..])
}

method add_by_inc(n: nat, m: nat) returns (r: nat)
  ensures r == m + n;
{
  r := n;
  var i := 0;
  while i < m
    invariant i <= m;
    invariant r == n + i;
  {
    i := i + 1;
    r := r + 1;
  }
  return r;
}
method add_by_inc_int(n : int, m : int) returns (r : int)
  ensures r == m + n;
{
  r := n;
  var sign := if m < 0 then -1 else 1;
  var m0 := if m < 0 then -m else m;
  var i := 0;
  while (i < m0)
    invariant i <= m0;
    invariant m < 0 ==> r == n - i;
    invariant m >= 0 ==> r == n + i;
  {
    i := i + 1;
    r := r + sign;
  }
}

method getEven(a : array?< int >) 
	requires a != null && a.Length > 0;
	modifies a;
	ensures forall i:int :: 0 <= i < a.Length ==> a[i] % 2 == 0;
{
	var index := 0;
	while index < a.Length 
		invariant 0 <= index <= a.Length;
		invariant forall j:int :: 0 <= j < index ==> a[j] % 2 == 0;
		decreases a.Length - index;
	{
		if (a[index] % 2 != 0) 
		{
			a[index] := a[index] + 1;
		}
		index := index + 1;
	}
}

module Search{  
  method FindMax(a: array<int>) returns (currentMaxIndex: int)
    requires a != null && a.Length >= 1;
    ensures 0 <= currentMaxIndex < a.Length;
    ensures forall k :: 0 <= k < a.Length ==> a[currentMaxIndex] >= a[k];
  {
    var index := 1;
    currentMaxIndex := 0;
    while(index < a.Length)
      decreases a.Length - index;
      invariant 0 <= currentMaxIndex < index <= a.Length;
      invariant forall k :: 0 <= k < index <= a.Length ==> a[currentMaxIndex] >= a[k];
    {
        if(a[index] > a[currentMaxIndex])
        {
          currentMaxIndex := index;
        }
        index := index + 1;
    }
  }
  method FindMin(a: array<int>) returns (currentMinIndex: int)
    requires a != null && a.Length >= 1;
    ensures 0 <= currentMinIndex < a.Length;
    ensures forall k :: 0 <= k < a.Length ==> a[currentMinIndex] <= a[k];
  {
    var index := 1;
    currentMinIndex := 0;
    while(index < a.Length)
      decreases a.Length - index;
      invariant 0 <= currentMinIndex < index <= a.Length;
      invariant forall k :: 0 <= k < index <= a.Length ==> a[currentMinIndex] <= a[k];
    {
        if(a[index] < a[currentMinIndex])
        {
          currentMinIndex := index;
        }
        index := index + 1;
    }
  }
  predicate sorted(a: array<int>, low: int, high : int)
    requires a != null && 0 <= low <= high < a.Length;
    reads a;
  {
    forall j, k :: low <= j < k <= high ==> a[j] <= a[k]
  }
}

predicate clean1(a:array?<int>,key:int)
     reads a
{
  (a == null) || (forall k::0<= k < a.Length  ==> a[k] != key)
}
method IsClean(a: array?<int>, key: int) returns (clean: bool)
    ensures clean1(a,key) == clean;
{
    var index := 0;
    if(a==null){
     assert a == null;
     assert a == null;
     return true;
    }
    assert a!= null;
    assert index == 0;
    while index < a.Length
       invariant 0<=index<= a.Length;
       invariant forall k :: 0 <= k < index ==> a[k] != key;
       invariant !(exists k | 0 <= k < index :: a[k] == key);
	   decreases (a.Length - index)
    {
      if(a[index] == key){
        assert a[index] == key;
        assert exists k :: 0<= k <= index ==> a[k] == key;
        assert a != null ==> exists k :: 0<= k <= index ==> a[k] == key;
        return false;
      }
      index :=index+1;
    }
    assert  !(exists k | 0 <= k < a.Length :: a[k] == key); 
    assert a != null && !(exists k :: 0<= k < a.Length ==> a[k] == key) ==> forall k :: 0 <= k < index ==> a[k] != key;
    return true;
}
