method computeDivision(x:int, y:int) returns (a:int, b:int)
requires x > 0 && y > 0
ensures x == y*a + b
ensures b < y
{
    var n:int := x;
    var cnt:int := 0;
    while(n >= y)
    decreases x - cnt * y
    invariant 0 <= n <= x;
    invariant cnt >= 0
    invariant (cnt * y + n) == x
    {
        cnt := cnt + 1;
        n := n - y;
    }
    return cnt, n;
}
function factorial(n:int):int
decreases n
requires n >= 0
{
    if(n <= 1) then 1 else n * factorial(n - 1)
}
method computeFactorial(x:int) returns (z:int)
requires x >= 0
ensures z == factorial(x)
{
    var n:int := 1;
    var acc := 1;
    while(n <= x)
    decreases x - n
    invariant 1 <= n <= x + 1
    invariant acc == factorial(n - 1)
    {
        acc := acc * n;
        n := n + 1;
    }
    return acc;
}
function fib(n:int):int
decreases n
requires n >= 0
{
    if(n <= 1) then n else fib(n-1) + fib (n-2)
}
method computeFibonacci(x:int) returns (z:int)
requires x >= 0
ensures z == fib(x)
{
    if(x <= 1) {
        return x;
    }
    var n:int := 2;
    var acc1:int := 1;
    var acc2:int := 0;
    assert acc1 + acc2 == fib(n);
    while(n <= x)
    decreases x - n
    invariant 2 <= n <= x + 1
    invariant acc1 == fib(n-1)
    invariant acc2 == fib(n-2)
    invariant acc1 + acc2 == fib(n)
    {
        var temp:int := acc1 + acc2;
        acc2 := acc1;
        acc1 := temp;
        n := n+1;
    }
    return acc1;
}
method indexOf(a:array<int>, n:int, elem:int) returns (z:int)
requires 0 <= n <= a.Length
ensures z < n
ensures (z >= 0) ==> a[z] == elem
ensures z < 0 <==> forall i :: (0 <= i < n) ==> a[i] != elem
{
    var idx:int := 0;
    while(idx < n)
    decreases n - idx
    invariant 0 <= idx <= n
    invariant forall i :: (0 <= i < idx) ==> a[i] != elem
    {
        if(a[idx] == elem){
            return idx;
        }
        idx := idx +1;
    }
    assert forall i :: (0 <= i < n) ==> a[i] != elem;
    return -1;
}
method max(a:array<int>, n:int) returns (maxVal:int, maxIdx:int)
requires 0 < n < a.Length
ensures 0 <= maxIdx < n
ensures forall i :: (0 <= i < n) ==> maxVal >= a[i]
{
    var i:int := 1;
    var mi:int := 0;
    while(i < n)
    decreases n - i
    invariant 0 <= i <= n
    invariant 0 <= mi < i
    invariant forall j :: (0 <= j < i) ==> a[mi] >= a[j]
    {
        if(a[i] >= a[mi]) {
            mi := i;
        }
        i := i + 1;
    }
    return a[mi], mi;
}
