function count(a:array<int>, n:int, v:int):int
decreases n
requires 0 <= n <= a.Length
reads a
{
    if (n == 0)
    then 0 
    else if (a[n-1] == v) then 1 + count(a, n-1, v) else count(a, n-1, v)
}
method Count(a:array<int>, v:int) returns (z:int)
requires true
ensures z == count(a, a.Length , v)
{
    var i:int := 0;
    var acc:= 0;
    assert acc == count(a, i, v);
    while(i < a.Length)
    decreases a.Length - i;
    invariant 0 <= i <= a.Length
    invariant acc == count(a, i, v)
    {
        if(a[i] == v) {
            acc := acc + 1;
        }
        i := i + 1;
    }
    return acc;
}

ghost function missing_number(nums: seq<nat>): nat
  requires |set i | i in nums| == |nums|
  requires forall x :: x in nums ==> 0 <= x <= |nums|
{
  var p := x => 0 <= x <= |nums| && x !in nums;
  assert exists x :: p(x) && forall y :: p(y) ==> y == x by {
    var range := set i | 0 <= i <= |nums|;
    assume |range| == |nums| + 1;
    var missing := range - set i | i in nums;
    assert |missing| == 1;
    var x :| x in missing;
    assert p(x);
    var y: int;
    forall y | p(y) ensures y in missing {}
    assume forall x, y :: x in missing && y in missing ==> x == y;
    forall y | p(y) ensures y == x {}
  }
  var x :| p(x);
  x
}

method moveZero(a: array<int>) returns (first0: int)
  requires a != null && a.Length > 0;
  modifies a;
  ensures a.Length == old(a.Length);
  ensures forall j :: 0 <= j < first0 <= a.Length ==> a[j] != 0;
  ensures forall j :: 0 <= first0 <= j < a.Length ==> a[j] == 0;
  ensures multiset(a[..]) == old(multiset(a[..]));
{
  first0 := 0;
  var cur := 0;
  while(cur < a.Length)
  invariant 0 <= first0 <= cur <= a.Length;
  invariant forall j :: 0 <= j < first0 <= a.Length ==> a[j] != 0;
  invariant forall j :: 0 <= first0 <= j < cur <= a.Length ==> a[j] == 0;
  invariant multiset(a[..]) == old(multiset(a[..]));
  {
    if(a[cur] != 0){
      swap(a, first0, cur);
      first0 := first0 + 1;
    }
    cur := cur + 1;
  }
  return first0;
}
method swap(a: array<int>, i: int, j: int)
  requires a != null && 0 <= i <= j < a.Length;
  modifies a;
  ensures a[i] == old(a[j]);
  ensures a[j] == old(a[i]);
  ensures multiset(a[..]) == old(multiset(a[..]));
  ensures forall k :: 0 <= k < a.Length && k != i && k != j ==> a[k] == old(a[k]);
{
  var temp := a[i];
  a[i] := a[j];
  a[j] := temp;
}

datatype List = Nil | Cons(int, List)
function Len(l:List): (v: int)
  ensures v >= 0
{
  match l
  case Nil => 0
  case Cons(hd, tl) => Len(tl) + 1
}
function isEmpty(l:List): bool
{
    match l
    case Nil => true
    case Cons(hd, tl) => false
}
function Stutter(l:List): (lout: List)
  ensures Len(lout) == Len(l) + 1 || Len(l) > 1 || l == lout
  ensures isEmpty(l) || Len(lout) > Len(l)
  ensures l == lout || Len(l) == 1 || Len(lout) > Len(l) + 1
{
  match l
  case Nil => Nil
  case Cons(hd, tl) => Cons(hd, Cons(hd, Stutter(tl)))
}

