method IncDec(x: int, y: int) returns (sum: int)
    ensures sum == x + y
{
    sum := 0;
    var tmp_x , tmp_y := x,y;
    while tmp_x != 0 || tmp_y != 0
        invariant x + y == sum + tmp_x + tmp_y
        decreases Abs(tmp_x) 
        decreases Abs(tmp_y)
    {
        if tmp_x < 0{
            sum := sum - 1;
            tmp_x:= tmp_x +1;
        }
        else if tmp_x > 0{
            sum := sum + 1;
            tmp_x := tmp_x - 1;
        }
        if tmp_y < 0{
            sum := sum - 1;
            tmp_y:= tmp_y + 1;
        }
        else if tmp_y > 0{
            sum := sum + 1;
            tmp_y := tmp_y -1;
        }
    }
}
