method CalculaQuociente(x: nat, y: nat) returns (d: nat, r: nat)
  requires x >= 0 && y > 0
  ensures r < y
  ensures x == y * d + r
{
  d := 0;
  r := x;
  while r >= y
    decreases r - y
    invariant x == y * d + r
    invariant r >= 0
  {
    r := r - y;
    d := d + 1;
  }
}

