predicate member(s: seq<int>, n: int) {
	exists i :: 0 <= i <|s| && s[i] == n
}
method Max1(s: seq<int>) returns (max:int)
	requires |s| > 0  			
	ensures member(s, max) 		
	ensures upperbound(s, max)	
{
	assert (|s| > 0);  
	assert 0 < 1 <= |s| && member(s[..1], s[0]);  
	max := s[0];
	assert 0 < 1 <= |s| && member(s[..1], max);  
	var i := 1;
	assert 0 < i <= |s| && member(s[..i], max);  
	while(i != |s|)
	invariant 0 < i <= |s|
	invariant member(s[..i], max); 
	invariant upperbound(s[..i], max); 
	{
		assert 0 < i <= |s| && member(s[..i], max) && i != |s|;			
		assert s[i]>max ==> 0 < i+1 <= |s| && member(s[..i+1], s[i]);  
		if (s[i] > max) { 
			assert 0 < i+1 <= |s| && member(s[..i+1], s[i]);	
			max := s[i];	
			assert 0 < i+1 <= |s| && member(s[..i+1], max);		
		}
		assert 0 < i+1 <= |s| && member(s[..i+1], max);			
		i := i + 1;
		assert 0 < i <= |s| && member(s[..i], max);				
	}
	assert member(s[..i], max) && i <= |s| && !(i != |s|);		
	assert member(s, max);										
}
