predicate isLeapYear(y: int) {
    y % 4 == 0 && (y % 100 != 0 || y % 400 == 0)
}
method WhichYear(daysSince1980: int) returns (year: int) {
    var days := daysSince1980;
    year := 1980;
    while days > 365
    decreases days
    {
        if isLeapYear(year) {
            if days > 366 {
                days := days - 366;
                year := year + 1;
            } else {
                break;
            }
        } else {
            days := days - 365;
            year := year + 1;
        }
    }
}

method copy( src: array<int>, sStart: nat
     , dest: array<int>, dStart: nat, len: nat)
    returns (r: array<int>)
  requires dest != null && src != null;
  requires src.Length >= sStart + len;
  requires dest.Length >= dStart + len;
  ensures r != null;
  ensures r.Length == dest.Length;
  ensures r[..dStart] == dest[..dStart];
  ensures r[dStart + len..] == dest[dStart + len..];
  ensures forall k: nat :: k < len
          ==> r[dStart + k] == src[sStart + k];
{
  if len == 0 { return dest; }
  var i: nat := 0;
  r := new int[dest.Length];
  while (i < r.Length)
    invariant i <= r.Length
    invariant forall k: nat :: k < i ==> r[k] == dest[k]
  {
    r[i] := dest[i];
    i := i + 1;
  }
  assert r[..] == dest[..];
  i := 0;
  while (i < len)
    invariant i <= len
    invariant r[..dStart] == dest[..dStart]
    invariant r[dStart..dStart + i] == src[sStart..sStart + i]
    invariant r[dStart + len..] == dest[dStart + len..]
  {
    r[dStart + i] := src[sStart + i];
    i := i + 1;
  }
}

