module HashsetInterface{
    class Hashset{
        ghost var Contents: set<int>;
        ghost var Repr: set<object>;
        predicate Valid()
            reads Repr, this;
        constructor (size: int)
            requires size > 0;
            ensures Valid();
            ensures fresh(Repr);
        method insert(e: int) returns (r: bool)
            modifies Repr, this;
            requires Valid();
            ensures Valid();
            ensures Repr == old(Repr);
            ensures !r ==> Contents == old(Contents);
            ensures r ==> Contents == old(Contents) + {e};
            ensures r == (e in Contents);
        method remove(e: int) returns (r: bool)
            modifies Repr, this;
            requires Valid();
            ensures Valid();
            ensures Repr == old(Repr);
            ensures Contents == old(Contents) - {e};
            ensures r == (e in old(Contents));
        method get(e: int) returns (r: int)
            requires Valid();
            ensures Valid();
            ensures Repr == old(Repr);
            ensures (r >= 0) == (e in Contents);
            ensures (r == -1) == (e !in Contents);
    }
}

module fast_exp{ 
lemma exp_even (x:real, n:nat)
  requires n % 2 == 0;
  ensures (exp (x, n) == exp (x*x, n/2));
{
  assume (false);
}
lemma exp_odd(x:real, n:nat)
  requires n % 2 == 1;
  ensures (exp (x, n) == exp (x*x, (n-1)/2)*x);
{
  assume (false);
}
function exp (x: real, n:nat): real
{
  if (x == 0.0) then 0.0
  else if (n == 0) then 1.0
  else x * exp(x, n-1)
}
method exp_by_sqr(x0: real, n0: int)returns (r:real)
		requires n0 >= 0
		requires x0 >= 0.0;
	  ensures r == exp (x0, n0);{
	if x0 == 0.0 {
		return 0.0;
	}
	if n0 == 0 {
		return 1.0;
	}
	var x, n, y := x0, n0, 1.0;
	while n > 1 
		invariant 1 <= n <= n0;
		  invariant exp (x0, n0) == exp (x, n) * y;{
		if n%2 == 0 {
			exp_even(x, n);
			x := x * x;
			n := n / 2;
		} else {
			exp_odd(x, n);
			y := x * y;
			x := x * x;
			n := (n - 1) / 2;
		}
	}
	return x * y;
}
}

